import React, { useState } from 'react'
import Constants from '../utilities/Constants'

export default function FileUploadForm(props){
    const initialFormData = Object.freeze({
        //Enter possible values here which are already displayed as an example
    });

    const [selectedFile, setSelectedFile] = useState();
    const [isFilePicked, setIsFilePicked] = useState(false);
    const [formData, setFormData] = useState(initialFormData);
    const [date, setDate] = useState(initialFormData)

    const handleChange = (e) => {
        setFormData({
            ...formData,
            [e.target.name]: e.target.value,
        });
    };

    const changeHandler = (event) => {
        setSelectedFile(event.target.files[0]);
        setIsFilePicked(true);
    }

    const handleSubmission = (e) => {
        e.preventDefault();

        var url;

        switch (window.$activeTable) {
        case 'Bing':
            url = `${Constants.API_URL_BASE}/BingTable/${date.month}/${date.year}`;
            break;
        case 'Google':
            url = `${Constants.API_URL_BASE}/GoogleTable/${date.month}/${date.year}`;
            break;
        case 'Lead':
            url = `${Constants.API_URL_BASE}/LeadTable`;
            break;
        case 'Order':
            url = `${Constants.API_URL_BASE}/OrderTable`;
            break;
        default:
            alert(`Error: Table with name "${window.$activeTable}" does not exist`)
            return;
        }

        formData.append('File', selectedFile);

        fetch(url, {
            method: 'POST',
            body: formData,
        })
        .then((response) => response.json())
        .then(responseFromServer => {
            console.log(responseFromServer);
        })
        .catch((error) => {
            console.error('Error:', error);
            alert(error);
        });
    }

    return(
        <form className="w-100 px-5">
            <h1 className="mt-5">Create new post</h1>

            <div className="mt-5">
                <input type="file" name="file" onChange={changeHandler} />
            </div>

            {(window.$activeTable === 'Bing' || window.$activeTable === 'Google') && showDateInput()}

            <div className="mt-5">
                <button onClick={handleSubmission} className="btn btn-dark btn-lg w-100 mt-2">Upload Excel File</button>
            </div>
        </form>
    )
    
    function showDateInput(){
        return(
            <div>
                <div className="mt-5">
                    <label className="h3 form-label">Post month</label>
                    <input value="Dez" name="month" type="text" className="form-control" onChange={handleChange} />
                </div>

                <div className="mt-5">
                    <label className="h3 form-label">Post year</label>
                    <input value="2022" name="year" type="text" className="form-control" onChange={handleChange} />
                </div>
            </div>
        )
    }
}
