      case 'Google':
        url = Constants.API_URL_GOOGLE_POSTS;
        break;
      case 'Lead':
        url = Constants.API_URL_LEAD_POSTS;
        break;
      case 'Order':
        url = Constants.API_URL_ORDER_POSTS;
        break;
      default:
        alert(`Error: Table with name "${window.$activeTable}" does not exist`)
        return;
    }

    fetch(url, {
      method: 'DELETE'
