import React, { useState } from 'react'
import Constants from '../utilities/Constants'

export default function TableFilter() {
    const initialFormData = Object.freeze({
        //Enter possible values here which are already displayed as an example
     });
 
     const [formData, setFormData] = useState(initialFormData);
 
     const handleChange = (e) => {
         setFormData({
             ...formData,
             [e.target.name]: e.target.value,
         });
     };

     const handleSubmit = (e) => {
        e.preventDefault();

        var url;

        switch (window.$activeTable) {
            case 'Bing':
              url = `${Constants.API_URL_BING_POSTS}/GetAny/${formData.col}/${formData.value}/${formData.exact}`;
              break;
            case 'Google':
              url = `${Constants.API_URL_GOOGLE_POSTS}/GetAny/${formData.col}/${formData.value}/${formData.exact}`;
              break;
            case 'Lead':
              url = `${Constants.API_URL_LEAD_POSTS}/GetAny/${formData.col}/${formData.value}/${formData.exact}`;
              break;
            case 'Order':
              url = `${Constants.API_URL_ORDER_POSTS}/GetAny/${formData.col}/${formData.value}/${formData.exact}`;
              break;
            default:
              alert(`Error: Table with name "${window.$activeTable}" does not exist`)
              return;
          }
     }

     return(
        <form className="w-100 px-5">
            <label className="h5 form-label">Filter:</label>
            <input value={formData.col} name="col" type="text" className="form-control" onChange={handleChange} />
            <input value={formData.value} name="value" type="text" className="form-control" onChange={handleChange} />
            <input value={formData.exact} name="exact" type="checkbox"/>
            <button onClick={handleSubmit} className="btn btn-dark btn-lg w-25 mt-5">Submit</button>
        </form>
     )
}
