import { useState } from "react"
import Table from "./table/table"
import Constants from "../../../utilities/Constants";

const TableScreen = () => {
    const [entries, setEntries] = useState([])
    const [activeTable, setActiveTable] = useState('')
    const [rows, setRows] = useState(100)
    
    const handleRowChange = (e) => {
        setRows(e.target.value)
        GetAllEntries(activeTable)
    };

    return(
        <>
            {(activeTable === '') && (
                <div className="row-sm">
                    <button onClick={() => GetAllEntries('Bing')} className="btn btn-dark btn-lg w-100 h-100">Bing Table</button>
                    <button onClick={() => GetAllEntries('Google')} className="btn btn-dark btn-lg w-100 h-100">Google Table</button>
                    <button onClick={() => GetAllEntries('Lead')} className="btn btn-dark btn-lg w-100 h-100">Lead Table</button>
                    <button onClick={() => GetAllEntries('Order')} className="btn btn-dark btn-lg w-100 h-100">Order Table</button>
                </div>
            )}
            {(activeTable !== '') && (
                <div className="row-sm">
                    <button onClick={() => setActiveTable('')} className="btn btn-dark btn-lg w-25 h-100">&#60;</button>
                    {(entries !== []) && (
                        <div>
                            <input value={rows} name="rows" type="text" onChange={handleRowChange}/> Rows
                            <Table data={entries} rowsPerPage={rows} type={activeTable}/>
                        </div>
                    )}
                </div>
            )}
        </>
    )

    function GetAllEntries(table) {
        setActiveTable(table);
        var url;
  
        switch (table) {
        case 'Bing':
            url = Constants.API_URL_BING_ENTRIES;
            break;
        case 'Google':
            url = Constants.API_URL_GOOGLE_ENTRIES;
            break;
        case 'Lead':
            url = Constants.API_URL_LEAD_ENTRIES;
            break;
        case 'Order':
            url = Constants.API_URL_ORDER_ENTRIES;
            break;
        default:
            alert(`Error: Table with name "${table}" does not exist`)
            return;
        }
      
        fetch(url, {
          method: 'GET'
        })
        .then(response => response.json())
        .then(entriesFromServer => {
          setEntries(entriesFromServer);
        })
        .catch((error) => {
          console.log(error);
          alert(error);
        });
    }
}

export default TableScreen
