import React, { useState, useEffect } from "react";
import { PieChart } from "./charts/pieChart";
import Constants from "../../../utilities/Constants";
import styles from "./graphs.module.css"
import axios from "axios";

var primaryScheme = ['#5fc431','#71d055','#83dc73','#96e890','#abf4ab','#c0ffc6','#a1e5ad','#82cc96','#62b37f','#429a6a','#188255']

//// MAPPING FUNCTIONS ////
function getImpressions(entries) {
    const data = []
    var others = 0;

    entries.map(function(entry){
        if(entry.impressions >= 5) {
            data.push({
                id: entry.terms,
                name: entry.terms, 
                value: entry.impressions
            })
        }
        else {
            others += entry.impressions;
        }
    })

    data.push({
        id: "others",
        name: "others",
        value: others
    })

    return data
}

function getClicks(entries) {
    const data = []
    var others = 0;

    entries.map(function(entry){
        if(entry.clicks >= 3) {
            data.push({
                id: entry.terms,
                name: entry.terms, 
                value: entry.clicks
            })
        }
        else {
            others += entry.clicks;
        }
    })

    data.unshift({
        id: "others",
        name: "others",
        value: others
    })

    return data
}

//// RENDER VIEW ////
const BingChart = () => {
    return(
        <div className={styles.grid_container}>
            <div className={styles.impressions_wrapper}>
                <h3>Impressions</h3>
                <PieChart data={GetAllEntries('impressions')} scheme={primaryScheme}/>
            </div>
            <div className={styles.clicks_wrapper}>
                <h3>Clicks</h3>
                <PieChart data={GetAllEntries('clicks')} scheme={primaryScheme}/>
            </div>
        </div>
    )
}

//// GETTER METHODS ////
function GetAllEntries(type){
    const url = Constants.API_URL_BING_ENTRIES;
    const [entries, setEntries] = useState([])

    useEffect(() => {
        axios.get(url)
        .then(res => {
            setEntries(res.data);
        })
    }, [])

    switch(type){
        case 'impressions':
            return getImpressions(entries)
        case 'clicks':
            return getClicks(entries)
    }
}

export default BingChart
