import { useState } from "react"
import axios from "axios";
import Table from "./table/table"
import FileUploadForm from "./forms/fileUploadForm";
import CreateEntryForm from "./forms/createEntryForm";
import Constants from "../../../utilities/Constants";

const TableScreen = () => {
    const [entries, setEntries] = useState([])
    const [activeTable, setActiveTable] = useState('')
    const [showFileUpload, setShowFileUpload] = useState(false)
    const [showCreateForm, setShowCreateForm] = useState(false)
    const [rows, setRows] = useState(100)
    
    const handleRowChange = (e) => {
        setRows(e.target.value)
        getAllEntries(activeTable)
    };

    return(
        <>
            {(activeTable === '' && showFileUpload === false && showCreateForm === false) && (
                <div className="row-sm">
                    <div>
                        <h3>Bing Search Terms</h3>
                        <button onClick={() => getAllEntries('Bing')} className="btn btn-dark btn-lg w-100 h-100">Show Table</button>
                        <button onClick={() => {setShowFileUpload(true); setActiveTable('Bing');}} className="btn btn-secondary btn-lg w-50 h-100">Upload Excel File</button>
                        <button onClick={() => exportToExcel('Bing')} className="btn btn-secondary btn-lg w-50 h-100">Export to Excel File</button>
                    </div>
                    <div>
                        <h3>Google Search Terms</h3>
                        <button onClick={() => getAllEntries('Google')} className="btn btn-dark btn-lg w-100 h-100">Show Table</button>
                        <button onClick={() => {setShowFileUpload(true); setActiveTable('Google');}} className="btn btn-secondary btn-lg w-50 h-100">Upload Excel File</button>
                        <button onClick={() => exportToExcel('Google')} className="btn btn-secondary btn-lg w-50 h-100">Export to Excel File</button>
                    </div>
                    <div>
                        <h3>Lead Entries</h3>
                        <button onClick={() => getAllEntries('Lead')} className="btn btn-dark btn-lg w-100 h-100">Show Table</button>
                        <button onClick={() => {setShowFileUpload(true); setActiveTable('Lead');}} className="btn btn-secondary btn-lg w-50 h-100">Upload Excel File</button>
                        <button onClick={() => exportToExcel('Lead')} className="btn btn-secondary btn-lg w-50 h-100">Export to Excel File</button>
                    </div>
                    <div>
                        <h3>Order Entries</h3>
                        <button onClick={() => getAllEntries('Order')} className="btn btn-dark btn-lg w-100 h-100">Show Table</button>
                        <button onClick={() => {setShowFileUpload(true); setActiveTable('Order');}} className="btn btn-secondary btn-lg w-50 h-100">Upload Excel File</button>
                        <button onClick={() => exportToExcel('Order')} className="btn btn-secondary btn-lg w-50 h-100">Export to Excel File</button>
                    </div>
                </div>
            )}
            {(showFileUpload === true) && (<FileUploadForm onFileUploaded={onFileUploaded} table={activeTable}/>)}
            {(showCreateForm === true) && (<CreateEntryForm onEntryCreated={onEntryCreated} table={activeTable}/> )}
            {(activeTable !== '' && showFileUpload === false && showCreateForm === false) && (
                <div className="row-sm">
                    <button onClick={() => setActiveTable('')} className="btn btn-dark btn-lg w-25 h-100">&#60;</button>
                    <button onClick={() => setShowCreateForm(true)} className="btn btn-success btn-lg w-50 h-100">Create New Entry</button>
                    <button onClick={() => deleteAllEntries()} className="btn btn-danger btn-lg w-25 h-100">Delete All Entries</button>
                    {(Object.keys(entries).length === 0) && (
                        <div>
                            Please upload a table to view its content
                        </div>
                    )}
                    {(Object.keys(entries).length !== 0) && (
                        <div>
                            <input className="mt-3" value={rows} name="rows" type="text" onChange={handleRowChange}/> Rows
                            <Table data={entries} rowsPerPage={rows} type={activeTable} getAllEntries={getAllEntries} deleteEntry={deleteSingleEntry}/>
                        </div>
                    )}
                </div>
            )}
        </>
    )

    //// INTERNAL METHODS ////
    function onFileUploaded(created){
        if(created) alert(`Sucessfully uploaded the file contents to "${activeTable}" Table.`)
        setShowFileUpload(false)
        setActiveTable('')
    }
    function onEntryCreated(created){
        if (created !== null) alert(`Entry succesfully created. After clicking OK, your new Entry will show up in the table below.`);
        setShowCreateForm(false)
        getAllEntries(activeTable);
    }

    //// SERVER REQUESTS ////
    function getAllEntries(table){
        setActiveTable(table);
        var url;
  
        switch (table) {
        case 'Bing':
            url = Constants.API_URL_BING_ENTRIES;
            break;
        case 'Google':
            url = Constants.API_URL_GOOGLE_ENTRIES;
            break;
        case 'Lead':
            url = Constants.API_URL_LEAD_ENTRIES;
            break;
        case 'Order':
            url = Constants.API_URL_ORDER_ENTRIES;
            break;
        default:
            alert(`Error: Table with name "${table}" does not exist`)
            return;
        }
      
        fetch(url, {
          method: 'GET'
        })
        .then(response => response.json())
        .then(entriesFromServer => {
          setEntries(entriesFromServer);
        })
        .catch((error) => {
          console.log(error);
          alert(error);
        });
    }

    function deleteAllEntries(){
        var url;
    
        switch (activeTable) {
          case 'Bing':
            url = Constants.API_URL_BING_ENTRIES;
            break;
          case 'Google':
            url = Constants.API_URL_GOOGLE_ENTRIES;
            break;
          case 'Lead':
            url = Constants.API_URL_LEAD_ENTRIES;
            break;
          case 'Order':
            url = Constants.API_URL_ORDER_ENTRIES;
            break;
          default:
            alert(`Error: Table with name "${activeTable}" does not exist`)
            return;
        }
    
        fetch(url, {
          method: 'DELETE'
        })
        .then(response => response.json())
        .then(responseFromServer => {
          console.log(responseFromServer);
          alert(`Sucessfully deleted Entries from "${activeTable}" Table.`);
          getAllEntries(activeTable)
        })
        .catch((error) => {
          console.log(error);
          alert(error);
        });
    }

    function deleteSingleEntry(id){
        var url;

        switch (activeTable) {
        case 'Bing':
            url = `${Constants.API_URL_BING_ENTRIES}/${id}`;
            break;
        case 'Google':
            url = `${Constants.API_URL_GOOGLE_ENTRIES}/${id}`;
            break;
        case 'Lead':
            url = `${Constants.API_URL_LEAD_ENTRIES}/${id}`;
            break;
        case 'Order':
            url = `${Constants.API_URL_ORDER_ENTRIES}/${id}`;
            break;
        default:
            alert(`Error: Table with name "${activeTable}" does not exist`)
            return;
        }

        fetch(url, {
            method: 'DELETE'
        })
        .then(response => response.json())
        .then(responseFromServer => {
            console.log(responseFromServer);
        })
        .catch((error) => {
            console.log(error);
            alert(error);
        });

        alert('Entry successfully deleted. After clicking OK, look at the table below to see your Entry disappear.');
        getAllEntries(activeTable);
    }

    function exportToExcel(table){
        var url;

        switch (table) {
        case 'Bing':
            url = `${Constants.API_URL_BING_ENTRIES}/ExportToExcel`;
            break;
        case 'Google':
            url = `${Constants.API_URL_GOOGLE_ENTRIES}/ExportToExcel`;
            break;
        case 'Lead':
            url = `${Constants.API_URL_LEAD_ENTRIES}/ExportToExcel`;
            break;
        case 'Order':
            url = `${Constants.API_URL_ORDER_ENTRIES}/ExportToExcel`;
            break;
        default:
            alert(`Error: Table with name "${table}" does not exist`)
            return;
        }

        /*fetch(url, {
            method: 'POST',
            headers: { 'content-type': 'application/json' }
            })
            .then(res => {return res.blob()})
            .catch(error =>
            {
                console.log(error)
            });

        alert(`Successfully exported the ${table} table.`);*/
        axios({
            url: 'your url',
            method: 'POST',       // Worked using POST or PUT. Prefer POST
            responseType: 'blob', // important
          }).then((response) => {
            const url = window.URL.createObjectURL(new Blob([response.data]));
            const link = document.createElement('a');
            link.href = url;
            link.setAttribute('download', 'file.pdf');
            document.body.appendChild(link);
            link.click();
          });
    }

    function apiDownloadRequest<T>(url: string, method: Method) {
        let instance = axios.create({  baseURL: "YOUR_BASE_URL" });  
        let options: AxiosRequestConfig = { 
          url,
          method,
          responseType: 'blob' // don't forget this
        };  
        return instance.request<any>(options)
          .then(response => { 
            let filename = response.headers['content-disposition']
              .split(';')
              .find((n: any) => n.includes('filename='))
              .replace('filename=', '')
              .trim();      
            let url = window.URL
              .createObjectURL(new Blob([response.data]));     
            saveAs(url, filename);    
        });
      }
}

export default TableScreen
