import { useState } from "react"
import Table from "./bingTable"
import Constants from "../../../utilities/Constants";

let entries;

const TableScreen = () => {
    const [showTable, setShowTable] = useState(false)
    const [entries, setEntries] = useState([])
    const [activeTable, setActiveTable] = useState()

    return(
        <>
            <div className="row-sm">
                <button onClick={() => GetAllEntries("Bing")} className="btn btn-dark btn-lg w-25 h-100">Bing Table</button>
                <button onClick={() => GetAllEntries("Google")} className="btn btn-dark btn-lg w-25 h-100">Google Table</button>
                <button onClick={() => GetAllEntries("Lead")} className="btn btn-dark btn-lg w-25 h-100">Lead Table</button>
                <button onClick={() => GetAllEntries("Order")} className="btn btn-dark btn-lg w-25 h-100">Bing Table</button>
            </div>
            {(showTable !== true) && (
                <Table data={entries} rowsPerPage={100}/>
            )}
        </>
    )

    function GetAllEntries(table) {
        const url = Constants.API_URL_BING_ENTRIES;
    
        setActiveTable(table);
      
        fetch(url, {
          method: 'GET'
        })
        .then(response => response.json())
        .then(entriesFromServer => {
          setEntries(entriesFromServer);
        })
        .catch((error) => {
          console.log(error);
          alert(error);
        });
    }
}

export default TableScreen
