import { useState } from "react";
import { PieChart } from "./charts/pieChart";
import Constants from "../../../utilities/Constants";
import styles from "./graphs.module.css"

var primaryScheme = ['#5fc431','#71d055','#83dc73','#96e890','#abf4ab','#c0ffc6','#a1e5ad','#82cc96','#62b37f','#429a6a','#188255']

//// MAPPING FUNCTIONS ////
function getImpressions(entries) {
    const data = []
    var others = 0;

    entries.map(function(entry){
        if(entry.impressions >= 5) {
            data.push({
                id: entry.terms,
                name: entry.terms, 
                value: entry.impressions,
                
            })
        }
        else {
            others += entry.impressions;
        }
    })

    data.push({
        id: "others",
        name: "others",
        value: others,
        color: "#f912fb"
    })

    return data
}

//// RENDER VIEW ////
const BingChart = () => {
    const [impressionsPie, setImpressionsPie] = useState([])
    setImpressionsPie(getImpressions(GetAllEntries()))

    return(
        <>
            <div className={styles.graph}>
                <PieChart data={impressionsPie} scheme={primaryScheme}/>
            </div>
            <div className={styles.graph}>
                <PieChart data={getImpressions(GetAllEntries())} scheme={primaryScheme}/>
            </div>
        </>
    )
}

//// GETTER METHODS ////
function GetAllEntries(){
    const url = Constants.API_URL_BING_ENTRIES;
    const [entries, setEntries] = useState([])
  
    fetch(url, {
      method: 'GET'
    })
    .then(response => response.json())
    .then(entriesFromServer => {
      setEntries(entriesFromServer);
    })
    .catch((error) => {
      console.log(error);
    });
    
    return entries
}

export default BingChart
