import { useState } from "react"
import Table from "./bingTable"
import { GetAllEntries } from "./methods"

let entries;

const TableScreen = () => {
    const [showTable, setShowTable] = useState(false)
    return(
        <>
            <div className="col-sm">
                <button onClick={() => setShowTable(true)} className="btn btn-dark btn-lg w-100 h-100">Bing Table</button>
            </div>
            {(showTable !== true) && (
                <Table data={GetAllEntries("Bing")} rowsPerPage={100}/>
            )}
        </>
    )

    function GetAllEntries(table) {
        const [entries, setEntries] = useState([])
        const url = Constants.API_URL_BING_ENTRIES;
    
        window.$activeTable = table;
      
        fetch(url, {
          method: 'GET'
        })
        .then(response => response.json())
        .then(entriesFromServer => {
          setEntries(entriesFromServer);
        })
        .catch((error) => {
          console.log(error);
          alert(error);
        });
    
        return entries;
    }
}

export default TableScreen
