import { ResponsivePie } from "@nivo/pie";

function getData(entries) {
    const data = []
    var others = 0;

    entries.map(function(entry){
        if(entry.impressions >= 5) {
            data.push({
                id: entry.terms,
                name: entry.terms, 
                value: entry.impressions,
                color: "#ff00ff"
            })
        }
        else {
            others += entry.impressions;
        }
    })

    data.push({
        id: "others",
        name: "others",
        value: others,
        color: "#f912fb"
    })

    return data
}

const BingChart = (props) => {
    return(
        <ResponsivePie
            data={getData(props.entries)}
            colors={['#5fc431','#71d055','#83dc73','#96e890','#abf4ab','#c0ffc6','#a1e5ad','#82cc96','#62b37f','#429a6a','#188255']}
            margin={{ top: 40, right: 80, bottom: 80, left: 80 }}
            innerRadius={0.5}
            padAngle={0.7}
            cornerRadius={3}
            activeOuterRadiusOffset={8}
            borderWidth={1}
            borderColor="#ffffff"
        />
    )
}

export default BingChart
