import { PieChart, Pie, Tooltip } from "recharts"
import { ResponsivePie } from "@nivo/pie";

/*const BingChart = (props) => {
    const data = []
    var others = 0;

    props.entries.map(function(entry){
        if(entry.impressions >= 5) {
            data.push({name: entry.terms, value: entry.impressions})
        }
        else {
            others += 1;
        }
    })

    data.push({name: "others", value: others})

    return(
        <PieChart width={600} height={600}>
        <Pie
          dataKey="value"
          isAnimationActive={false}
          data={data}
          cx="50%"
          cy="50%"
          outerRadius={260}
          fill="#8884d8"
          label
        />
        <Tooltip />
      </PieChart> 
    )
}*/

function getData(entries) {
    const data = []
    var others = 0;

    entries.map(function(entry){
        if(entry.impressions >= 5) {
            data.push({
                id: entry.terms,
                name: entry.terms, 
                value: entry.impressions,
                color: "#ff00ff"
            })
        }
        else {
            others += 1;
        }
    })

    data.push({
        id: "others",
        name: "others",
        value: others,
        color: "#f912fb"
    })

    return data
}

const BingChart = (props) => {
    return(
        <ResponsivePie
            data={getData(props.entries)}
            colors={['#5fc431','#71d055','#83dc73','#96e890','#abf4ab','#c0ffc6','#a1e5ad','#82cc96','#62b37f','#429a6a','#']}
            margin={{ top: 40, right: 80, bottom: 80, left: 80 }}
            innerRadius={0.5}
            padAngle={0.7}
            cornerRadius={3}
            activeOuterRadiusOffset={8}
            borderWidth={1}
            borderColor="#ffffff"
            arcLinkLabelsSkipAngle={10}
            arcLinkLabelsTextColor="#333333"
            arcLinkLabelsThickness={2}
            arcLinkLabelsColor={{ from: 'color' }}
            arcLabelsSkipAngle={10}
            arcLabelsTextColor={{
                from: 'color',
                modifiers: [
                    [
                        'darker',
                        2
                    ]
                ]
            }}
        />
    )
}

export default BingChart
