import React, { useState, useEffect } from "react";
import { PieChart } from "./charts/pieChart";
import Constants from "../../../utilities/Constants";
import styles from "./graphs.module.css"
import axios from "axios";

var primaryScheme = ['#5fc431','#71d055','#83dc73','#96e890','#abf4ab','#c0ffc6','#a1e5ad','#82cc96','#62b37f','#429a6a','#188255','#429a6a','#62b37f','#82cc96','#a1e5ad','#c0ffc6','#abf4ab','#96e890','#83dc73','#71d055']

//// MAPPING FUNCTIONS ////
function getMatrices(entries, minImpr) {
    const data = []
    var serum = 0;
    var K3EDTA = 0;
    var EDTA = 0;
    var Li = 0;
    var other = 0;

    entries.map(function(entry){
        // if(entry.matrix == "serum") {
        //     serum = serum + 1;
        // }
         switch(entry.matrix) {
             case "serum ":
                 serum++;
                 return
             case "K3EDTA plasma ":
                 K3EDTA++;
                 return
             case "EDTA plasma ":
                 EDTA++;
                 return
             case "Li-heparin plasma ":
                 Li++;
                 return
             default:
                 other++;
                 return
         }
    })
    data.push({
        id: "serum", 
        value: serum
    })
    data.push({
        id: "K3EDTA plasma", 
        value: K3EDTA
    })
    data.push({
        id: "EDTA plasma", 
        value: EDTA
    })

    return data
}

//// RENDER VIEW ////
const OrderChart = () => {
    const [minImpr, setMinImpr] = useState(25)

    return(
        <div className={styles.grid_container}>
            <div className={styles.impressions_wrapper}>
                <h3>Matrix</h3>
                <PieChart data={GetAllEntries('matrix', minImpr)} scheme={primaryScheme}/>
                <div className={styles.min}>Matrix: </div>
            </div>
        </div>
    )
}

//// GETTER METHODS ////
function GetAllEntries(type, props){
    const url = Constants.API_URL_ORDER_ENTRIES;
    const [entries, setEntries] = useState([])

    useEffect(() => {
        axios.get(url)
        .then(res => {
            setEntries(res.data);
        })
    }, [])

    return getMatrices(entries)
}

export default OrderChart
