import { PieChart, Pie, Tooltip } from "recharts"
import { ResponsivePie } from "@nivo/pie";

/*const BingChart = (props) => {
    const data = []
    var others = 0;

    props.entries.map(function(entry){
        if(entry.impressions >= 5) {
            data.push({name: entry.terms, value: entry.impressions})
        }
        else {
            others += 1;
        }
    })

    data.push({name: "others", value: others})

    return(
        <PieChart width={600} height={600}>
        <Pie
          dataKey="value"
          isAnimationActive={false}
          data={data}
          cx="50%"
          cy="50%"
          outerRadius={260}
          fill="#8884d8"
          label
        />
        <Tooltip />
      </PieChart> 
    )
}*/

function getData(entries) {
    const data = []
    var others = 0;

    entries.map(function(entry){
        if(entry.impressions >= 5) {
            data.push({
                id: entry.terms,
                name: entry.terms, 
                value: entry.impressions,
                color: "#ff00ff"
            })
        }
        else {
            others += 1;
        }
    })

    return data
}

const BingChart = (props) => {
    var data = getData(props.entries)
    alert

    return(
        <ResponsivePie
            data={data}
            colors={{ scheme: 'greens'}}
            margin={{ top: 40, right: 80, bottom: 80, left: 80 }}
            innerRadius={0.5}
            padAngle={0.7}
            cornerRadius={3}
            activeOuterRadiusOffset={8}
            borderWidth={1}
            borderColor="#ffffff"
            arcLinkLabelsSkipAngle={10}
        arcLinkLabelsTextColor="#333333"
        arcLinkLabelsThickness={2}
        arcLinkLabelsColor={{ from: 'color' }}
        arcLabelsSkipAngle={10}
        arcLabelsTextColor={{
            from: 'color',
            modifiers: [
                [
                    'darker',
                    2
                ]
            ]
        }}
        defs={[
            {
                id: 'dots',
                type: 'patternDots',
                background: 'inherit',
                color: 'rgba(255, 255, 255, 0.3)',
                size: 4,
                padding: 1,
                stagger: true
            },
            {
                id: 'lines',
                type: 'patternLines',
                background: 'inherit',
                color: 'rgba(255, 255, 255, 0.3)',
                rotation: -45,
                lineWidth: 6,
                spacing: 10
            }
        ]}
        fill={[
            {
                match: {
                    id: 'ruby'
                },
                id: 'dots'
            },
            {
                match: {
                    id: 'c'
                },
                id: 'dots'
            },
            {
                match: {
                    id: 'go'
                },
                id: 'dots'
            },
            {
                match: {
                    id: 'python'
                },
                id: 'dots'
            },
            {
                match: {
                    id: 'scala'
                },
                id: 'lines'
            },
            {
                match: {
                    id: 'lisp'
                },
                id: 'lines'
            },
            {
                match: {
                    id: 'elixir'
                },
                id: 'lines'
            },
            {
                match: {
                    id: 'javascript'
                },
                id: 'lines'
            }
        ]}
        legends={[
            {
                anchor: 'bottom',
                direction: 'row',
                justify: false,
                translateX: 0,
                translateY: 56,
                itemsSpacing: 0,
                itemWidth: 100,
                itemHeight: 18,
                itemTextColor: '#999',
                itemDirection: 'left-to-right',
                itemOpacity: 1,
                symbolSize: 18,
                symbolShape: 'circle',
                effects: [
                    {
                        on: 'hover',
                        style: {
                            itemTextColor: '#000'
                        }
                    }
                ]
            }
        ]}
        />
    )
}

export default BingChart
